import { defineComponent, ref, computed } from 'vue';

export default defineComponent({
  name: 'DataTable',
  props: {
    data: {
      type: Array,
      required: true
    },
    columns: {
      type: Array,
      required: true
    }
  },
  setup(props) {
    const currentPage = ref(1);
    const pageSize = ref(10);
    const searchQuery = ref('');
    const sortKey = ref('');
    const sortOrders = {
      asc: 1,
      desc: -1
    };

    const filteredData = computed(() => {
      const queryLower = searchQuery.value.toLowerCase();
      return props.data.filter(row => {
        return Object.values(row).some(value =>
          value.toString().toLowerCase().includes(queryLower)
        );
      });
    });

    const sortedData = computed(() => {
      if (!sortKey.value) return filteredData.value;
      return [...filteredData.value].sort((a, b) => {
        let sortValueA = a[sortKey.value];
        let sortValueB = b[sortKey.value];
        return sortOrders[sortOrders[sortKey.value] === 1 ? sortKey.value : 'asc'] *
          (sortValueA < sortValueB ? -1 : (sortValueA > sortValueB ? 1 : 0));
      });
    });

